{	char 					*line;	int 					err;	err = 0;	line = NULL;	get_num_ants(fd, map, line);	while ((err = get_next_line(fd, &line)) == 1)	{		if (line[0] == '#')			process_comment(line, map, fd);		else if (isdigit(line[0]) || isalpha(line[0]))			process_room(line, map, NONE);		else		{			free(line);			error(INVALID_LINE_ERR, map);		}		free(line);	}	free(line);	if (err < 0)		error(FILE_READ_ERR, map);	if (!map->start)		error(NO_START_ERR, map);	if (!map->end)		error(NO_END_ERR, map);}